mapper.select:
        select *
        from (
        select rownum as rn, a.*
        from (
        
        ) a
        )
        where rownum  <=  #{pageSize}
        and rn  >  (#{pageNo} - 1) * #{pageSize}

mapper.update:
UPDATE EMP
SET
ENAME = #{eName}
, JOB = #{job}
, MGR = #{mgr}
, HIREDATE = #{hireDate}
, SAL = #{sal}
, COMM = #{comm}
, DEPTNO = #{deptNo}
WHERE
EMPNO = #{empNo}
